(10, '7Z4U6U0C', 'Air Purifier', '\"[{\"text\":\"High Efficiency Purification with filter. Comes with three-layer filter effectively removes PM2.5, pollen, formaldehyde.\"},{\"text\":\"Perfect for medium and large rooms, with PM CADR of 13,420 cubic foot, it can purifies the 107 sq foot area for 12 times per hour.\"},{\"text\":\"Touch Display. Touch to switch the working mode - Sleep, Auto, Favorite, Low, Medium and High level.\"},{\"text\":\"Ultra-Silent Sleep Mode. It works quietly with the Noise Level as low as 38dB. The 3 main modes (Automatic, Night and Manual) can be selected according to the air quality and space needs.\"},{\"text\":\"Dimensions: 9 x 9 x 20 inches. Weight: 14 pounds.\"}]\"', 141, 'gadget', 'img10_7Z4U6U0C.png', 'https://www.BigToolIndustry.com/img/img12.png', 0, NULL, 0),
(10, '1O1U2P6N', 'Air Purifier for Home', '\"[{\"text\":\"3 part filtration system captures 99 percentage of common airborne pm 2.5 pollutants such as allergens, odors, smoke, mold spores, dust mites and pet dander\"},{\"text\":\"Activated carbon filter removes common odors caused by smoke, pets, cooking, dorm room smells and gases (VOCs) as well as smoke caused by wildfires\"},{\"text\":\"Quiet operation enabled by advanced filters that allow for a higher airflow at a lower noise level. Sound level: 17 \\u2013 46 decibel\"}]\"', 149, 'gadget', 'img10_1O1U2P6N.png', 'https://www.SmarterTechShop.com/img/img24.png', 0, NULL, 0),
(8, '0S4G0V8G', 'Air Quality Monitor', '\"[{\"title\":\"Know Your Air\",\"text\":\"Smart Air Quality Monitor makes it easy to understand what\\u2019s in your indoor air.\"},{\"title\":\"Track And Measure\",\"text\":\"Keep tabs on 5 key factors: particulate matter (PM 2.5), volatile organic compounds (VOCs), carbon monoxide (CO), humidity, and temperature.\"},{\"title\":\"Stay Informed\",\"text\":\"Get an indication of current air quality from the color-coded LED, and an easy-to-understand air quality score in the Alexa app anytime.\"},{\"title\":\"Get Notifications\",\"text\":\"If Alexa detects poor air quality, you can get notifications on your phone or even announcements on Echo devices.\"},{\"title\":\"Certified for Humans\",\"text\":\"Smart home made easy for non-experts. Setup with Alexa is simple.\"}]\"', 109, 'gadget', 'img8_0S4G0V8G.png', 'https://www.SuccessProWidget.com/img/img8.png', 0, NULL, 0),
