(2, '4V5Q4D6U', 'Butter Dish', '\"[{\"text\":\"Butter dish comes with clear plastic cover and stainless pan.\"},{\"text\":\"Clear plastic cover with chrome finish knob. Fits one stick of butter.\"},{\"text\":\"Attractive mirror polished pan crafted of durable stainless steel.\"},{\"text\":\"Dimensions: 7.25 x 5 x 3 inches. Weight: 8.8 ounces.\"}]\"', 7.95, 'gadget', 'img2_4V5Q4D6U.png', 'https://www.ExtraFunWidget.com/img/img17.png', 0, NULL, 0),
(6, '8H0V0R5M', 'CPU Air Cooler', '\"[{\"text\":\"4 Heat Pipe with exclusive direct contact Technology effectively provides excellent heat dissipation; Air flow: 42 CFM; Noise level: 26.0 decibels.\"},{\"text\":\"Stacked fin array ensures least airflow resistance which allows cooler air flow into the heatsink.\"},{\"text\":\"Dimensions: 6.01 x 5.12 x 3.88 inches; Weight: 3.15 Pounds; Material: Aluminum; Color: Black + RGB\"}]\"', 69.99, 'gadget', 'img6_8H0V0R5M.png', 'https://www.RealMasterTopGadget.com/img/img6.jpg', 0, NULL, 0),
(8, '2F7L8P0O', 'CPU Cooler', '\"[{\"text\":\"Greatly reduced humming and higher efficiency. Like the other components, the pump is also PWM controlled. This leads to a significantly lower noise level at low and typical loads, but also to much lower power consumption, with a maximum of of 0.7 W at no load, 2.1 W under normal load and a maximum of 3.4 W under full load for the entire AiO.\"}]\"', 109, 'gadget', 'img8_2F7L8P0O.png', 'https://www.ProTechProMaster.com/img/img8.jpg', 0, NULL, 0),
(3, '6K1U7D0W', 'Cable 16 FT for VR Headset and Gaming PC', '\"[{\"text\":\"Connector Type \\tUsb 3.0\"},{\"text\":\"Cable Type \\tUSB\"},{\"text\":\"Compatible Devices \\tPersonal Computer\"},{\"text\":\"Color \\tBlack\"},{\"text\":\" High Speed Charging and Data Transfer: Compatible with oculus software, tested at over 2000 Mbit\\/s data transfer rate with link cable for Oculus Quest 2\"},{\"text\":\"Long Enough and Flexible: 16 feet long cable allows you to move freely without by restricted when playing games and watching movies or listening to music, bringing a good experience.\"},{\"text\":\"User Friendly Design: The USB Type C port with a 90\\u00b0 angled design prevents it from disconnecting from Oculus Quest 2 easily.\"},{\"text\":\"Clean & Tidy - Comes with 3 cable clips, you can use them to fix the extra cable on the wall or desktop;\"},{\"text\":\"Compatible with Oculus Quest 2: the link cable connects Oculus Quest 2 to a gaming PC so that you can play all Oculus Rift apps and Steam VR games, providing perfect PC VR experience. The color and design of the cable match perfectly with the Quest 2 headset.\"},{\"text\":\"Flexible 16 FT Length Cable: Reaching 16 ft long, this cable gives you maximum freedom of movement without being restricted when you are immersed in the VR world.\"},{\"text\":\"High Speed Transfer and Safe Charging: This cable supports USB 3.0 transmission speed and charging with a maximum current of 3A. Stable and fast data synchronization working with sustained and reliable current brings you a smooth gaming experience. Whether it can be charged while playing depends on the output power of the USB port of the gaming PC. It should be noted that the maximum output power of the computer\'s USB port is 5V&1A.\"},{\"text\":\"User Friendly Design: The Type C port with an L shaped design is comfortable to use with your Quest VR headset. The attached Velcro strap fixes the cable and Quest to prevent disconnection during movement.\"}]\"', 19.99, 'gadget', 'img3_6K1U7D0W.png', 'https://www.ExcellentLifeDevice.com/img/img2.jpg', 0, NULL, 0),
(2, '8Q8T3S4H', 'Cable Management Sleeve, 19-20 Inch Cord Organizer (4 pack)', '\"[{\"text\":\" Set of 4, each 19 - 20\\\" long. Flexible neoprene cable sleeve with zip-up solution manages and conceals cables while providing easy access\"},{\"text\":\"Form-fitting neoprene stretchy material allows for multiple cables and flexibility (each sleeve can hold up to 8-10 cables). Could double the capacity when zip two sleeves together\"},{\"text\":\"Easy to use, just gather cables together, wrap the sleeve around and zip-up, perfect for home and office use\"},{\"text\":\"Ideal for keeping cords organized behind the TV entertainment system, computer monitor, etc.\"},{\"text\":\"Pack of 4, each 19-20\\\" long (1.2\\\" diameter when zip-up) \"}]\"', 7.95, 'gadget', 'img2_8Q8T3S4H.png', 'https://www.AllToolGarden.com/img/img22.png', 0, NULL, 0),
