(10, '6H8L1K8E', 'Fabric Steam Press', '\"[{\"text\":\"This Fabric Steam Press is your solution for a professional quality pressing in the comfort of your own home. Delivering dry-cleaners results without the hassle, cost or chemicals, the Fabric Steam Press smoothes wrinkles and creates crisp creases in seconds. At the press of a button, bursts of hot steam penetrate deep into fabrics, relaxing fibers, releasing wrinkles and freshening clothes in seconds. The heated non-stick pressing plate safely presses a wide variety of fabrics, with selectable temperature settings categorized by types of material. With a pressing surface that measures 20 x 8 inches, the Fabric Steam Press is the fastest, easiest, and most cost-effective way to get the professional look you want.\"},{\"text\":\"Dimensions: 20.47 x 17.16 x 6.73 inches; Weight: 15.42 pounds; Color: Black\"}]\"', 149, 'gadget', 'img10_6H8L1K8E.png', 'https://www.ProGadgetTech.com/img/img16.png', 0, NULL, 0),
(5, '2Q3I2R7J', 'Facial Cleansing Brush', '\"[{\"text\":\"The waterproof body & facial brush is the perfect in-shower supply for skin cleaning & exfoliation - with 2 speed settings and 2 detachable brush heads for optimal body cleanse, including the spa effect of a soothing massage. The electric body brush provides a hassle-free and cordless cleanse, featuring an easy to use one-button operation and 60 minute battery life, charging cable included.\"},{\"text\":\"Dimensions: 16 x 3.9 x 3.9 inches; Weight: 0.5 Pound; Power Source: Battery; Color: Pink\"}]\"', 39.99, 'gadget', 'img5_2Q3I2R7J.png', 'https://www.AllToolGarden.com/img/img4.png', 0, NULL, 0),
(7, '3D4Z1I5D', 'Fan with Remote', '\"[{\"text\":\"2-Blade oscillating pedestal fan with automatic oscillation and adjustable height and vertical angle\"},{\"text\":\"Engineered for whisper-quiet operation; High quality silent motor works at low noise level\"},{\"text\":\"3 power settings, and 3 breeze modes (nature, sleep, normal) with wide oscillation for cooling medium to large rooms\"},{\"text\":\"Easy digital operation, auto on\\/off timer, and remote control for changing modes from across the room\"},{\"text\":\"120 volt \\/ 55 watt power consumption\"},{\"text\":\"Dimensions: 17.72 x 15.75 x 53.15 inches; Weight: 12.3 pounds; Material: Polymer and Metal; Color: Black\"}]\"', 89.95, 'gadget', 'img7_3D4Z1I5D.png', 'https://www.WidgetWonderworld.com/img/img7.png', 0, NULL, 0),
(3, '2Q1B4W4Y', 'Fast Charger With 6ft USB-C', '\"[{\"text\":\"At 50% smaller than a standard 20W charger, provides more power while saving space in your bag or while plugged into a wall outlet.\"},{\"text\":\"Dimensions: 1.61 x 1.53 x 1.06 inches; Weight: 8.4 ounces; Color: White\"}]\"', 19.99, 'gadget', 'img3_2Q1B4W4Y.png', 'https://www.BestFriendlyWidget.com/img/img4.png', 0, NULL, 0),
(4, '0G7A4R1V', 'Fillet Knife', '\"[{\"text\":\"Removable Blades. 7\\\" Stainless Steel Blade. 7\\\" Stainless Steel Flex Blade.\"},{\"text\":\"6\' 120 volt AC power cord. Smith\'s Fillet XL Fillet Glove.\"},{\"text\":\"Marine grade mesh storage bag.\"},{\"text\":\"Included components: Electric Knife; 8\\\" Flex Fillet Blade w\\/Sheath; 8\\\" Fillet Blade w\\/Sheath; Fillet Glove; Storage Bag; Packaging.\"},{\"text\":\"Dimensions: 14.41 x 7.2 x 3.46 inches. Weight: 0.93 Kilograms.\"}]\"', 24.99, 'gadget', 'img4_0G7A4R1V.png', 'https://www.ExploreGadgetWorld.com/img/img3.png', 0, NULL, 0),
(10, '7M2C6B1E', 'Film & Slide Scanner', '\"[{\"text\":\"FILM TO JPEG IN SECONDS \\u2013 Powerful 14\\/22MP Film Scanner Converts Old 35mm, 126, 110, Super 8 & 8mm Negatives & Slides to JPEG Digital Files - NOTE; THE SCANZA WILL NOT CONVERT FILMS OTHER THEN 35mm, 126, 110, Super 8 & 8mm Negatives.\"},{\"text\":\"LARGE, BRIGHT 3.5\\u201d TFT LCD \\u2013 High Definition Built-In Color Display Features Adjustable Brightness & Convenient Tilt for Easy Operation & Image Viewing.\"},{\"text\":\"AN ADAPTER FOR EVERYTHING \\u2013 Unit Arrives w\\/Multiple Film Inserts & Adapters for Fast, Flexible Operation; Big One-Touch Buttons Allow for One-Step Scan & Save.\"},{\"text\":\"INTUITIVE USER INTERFACE \\u2013 Includes Helpful Tray & Insert Directory; Easily Edit Image RGB & Resolution, Browse Gallery [SD Card NOT Included, supports up to 128GB] & More.\"},{\"text\":\"CABLES & EXTRAS INCLUDED \\u2013 Mac & PC Compatible Converter Comes w\\/USB Power Cable, HDMI Cable, AC Adapter, Video Cable & FREE Film Cleaning Brush. Use the included brush to also clean the light box of dust and speckles, to avoid marks on the scanned images.\"}]\"', 149, 'gadget', 'img10_7M2C6B1E.png', 'https://www.ThePowerfulWidget.com/img/img13.png', 0, NULL, 0),
(2, '4R1E6O6L', 'Finger Ice Compress', '\"[{\"text\":\"Ice Pack for Relief: Suitable for relieving arthritis, carpal tunnel, sprains, minor burns and injuries such as football, basketball, volleyball, rock climbing, judo mixed martial arts, wrestling, dance, etc. Cool finger pack is simply wipe them down to cleans.\"},{\"text\":\"Lightweight Gel ice Pack: This finger ice pack is small in size and light in weight, so it won\'t be very oppressive when applied to the painful place, ice pack will not cause discomfort. Ice packs by finger are suit for arthritis, carpal tunnel, easy to use.\"},{\"text\":\"Reusable Cold Pack: The ice pack can be froze in the refrigerator after use and reused for the use. Great help for people with repeated injuries or pain. Finger gel ice bag provides full coverage around the hand.\"},{\"text\":\"Gel Ice Packs Easy to Use: No need to freeze hands, feet or fingertips into ice water to compress painful fingers and toes, just place our ice pack over the painful area. Cold finger gel bag is stays securely in place for hands free use. freezer wrap is one size fits most.\"},{\"text\":\"Multi Scene Application: The ice pack is inserted into the fingertips or toes for immediate relief, cooling and compression, which can meet your daily pain needs. Finger sleeve is easy to use, ice or heat packs for quick and easy relief.\"},{\"text\":\"Dimensions: 2.36 x 1.97 x 1.57 inches. Weight: 1.76 Ounces.\"}]\"', 6.95, 'gadget', 'img2_4R1E6O6L.png', 'https://www.BIgWorldWidget.com/img/img16.png', 0, NULL, 0),
