(10, '0E1T8A3U', 'Jumperoo', '\"[{\"text\":\"Item arrives in frustration-free, easy-to-open packaging that\\u2019s 100% recyclable.\"},{\"text\":\"Exciting lights, sounds and music reward your baby\'s every jump\"},{\"text\":\"Infant jumper with 360 degrees of play, including peek-a-boo tiger, bat-at monkey, rattle lizard, and more\"},{\"text\":\"3 adjustable height positions to grow with your baby\"},{\"text\":\"No doorway required, plus fabric spring covers keep little fingers safe\"},{\"text\":\"Soft, comfy seat pad is machine washable and dryer safe\"},{\"text\":\"Durable frame is easy to assemble\"},{\"text\":\"Dimensions: 37 x 32 x 32 inches; Weight: 16.06 pounds; Material: Plastic; Color: Rainforest\"}]\"', 141, 'gadget', 'img10_0E1T8A3U.png', 'https://www.WidgetWonderworld.com/img/img12.png', 0, NULL, 0),
(3, '3W8K6R1F', 'Key Protector Box', '\"[{\"text\":\"How would you feel if your car was stolen off your drive while you sleep? A car is now stolen every two minutes, this faraday box will prevent keyless car key theft. Thieves are able to amplify your keyless car signal remotely by using a signal booster device while you sleep. Our premium car theft prevention box is lined with a high-quality material that stops any signal leaving the box. Simply placing your key in to the closed faraday box will prevent your car key signal from being accessible by thieves. Protecting your car at all times.\"},{\"text\":\"Store multiple keys in one place. The box will allow you to store not only your key but also your spare key and family members keys all in one secure and safe place giving you the peace of mind that your whole family are protected. Many people will overlook their spare key fob and leave it unprotected, now with this box you can store all keys in one safe place. When tested, anywhere between six to eight sets of car keys can fit in the box.\"}]\"', 19.99, 'gadget', 'img3_3W8K6R1F.png', 'https://www.JoyfulGadget.com/img/img2.jpg', 0, NULL, 0),
(5, '8D3C7U3G', 'Keyless Entry Door Lock', '\"[{\"text\":\"You can remotely generate temporary codes to guests, no need to worry about getting keys back from them.\"},{\"text\":\"Dimensions: 3.54 x 2.56 x 0.51 inches; Weight: \\u200e1.38 pounds; Color: \\u200eBlack; Special Features: \\u200eAuto-Locking, One-Touch Locking, 20 Customizable User Code\"}]\"', 49.95, 'gadget', 'img5_8D3C7U3G.png', 'https://www.ProtechionWorld.com/img/img5.png', 0, NULL, 0),
(2, '1B7B7F7T', 'Kids Microfiber Bathroom Shower Curtain', '\"[{\"text\":\"This durable curtain adds sophisticated polish to a master, family, or guest bath. Mix and match with towels and other accessories.\"},{\"text\":\"Made with durable, wrinkle-resistant 100% polyester fabric, the shower curtain is built to last and can be machine washed on cold and dried on low.\"},{\"text\":\"Featuring ring holes along the top seam for hanging on an existing shower rod, you can easily install the curtain in any bathroom in the house.\"},{\"text\":\"Fits standard bathtub showers.\"},{\"text\":\"Dimensions: 72 x 72 inches; Weight: 13.3 ounces; Material: Microfiber; Color: Multicolor\"}]\"', 7.95, 'gadget', 'img2_1B7B7F7T.png', 'https://www.ChoiceProTechMaster.com/img/img16.png', 0, NULL, 0),
