(8, '3O8C0C4V', 'Pasta Attachment for Stand Mixers', '\"[{\"text\":\"This Pasta Attachment is designed for stand mixers. Made of Stainless Steel, which is sturdy and durable, far superior to plastic shell models. Gives you the perfect experience of pasta. Package includes pasta sheet roller, fettuccine cutter, spaghetti cutter, cleaning brush and user manual.\"},{\"text\":\"Package Dimensions: 11 x 10.3 x 4.2 inches; Weight: 5.8 Pounds; Number of settings: 8; Material: Stainless Steel; Color: Stainless Steel\"}]\"', 119, 'gadget', 'img8_3O8C0C4V.png', 'https://www.TheGreatWidgetItem.com/img/img9.png', 0, NULL, 0),
(2, '1K6K2E3W', 'Pastry Crimper', '\"[{\"text\":\"Crimp and seal pies, turnovers, ravioli and tarts by rolling around edges of dough.\"},{\"text\":\"You\'ll have great looking, securely sealed pastries and pasta in no time.\"},{\"text\":\"Slotted metal wheel with a solid wood handle.\"},{\"text\":\"Hand washing recommended.\"},{\"text\":\"Dimensions: 7 x 1 x 2.25 inches. Weight: 1.44 ounces.\"}]\"', 7.95, 'gadget', 'img2_1K6K2E3W.png', 'https://www.AmazedTechWorld.com/img/img17.png', 0, NULL, 0),
(2, '6Z0P8C3Q', 'Pastry/Pizza Roller', '\"[{\"text\":\"To help achieve a perfectly cooked crust, it needs to be of a uniform thickness. Pastry\\/Pizza Roller is the perfect 2-in-1 tool for pies, cookies and pizza dough!\"},{\"text\":\"The large roller smoothly rolls out dough, creating a uniform crust. Easy to use on your pastry mat\\/cutting board or directly in the pan!\"},{\"text\":\"The small roller makes it easy to fill in dough to the edges, smooths edges and sides, and ensures uniform depth around the inner rim of the pan.\"},{\"text\":\"Also ideal to crush nuts, crackers, cookies and bread crumbs!\"},{\"text\":\"Dimensions: 7 x 1.25 x 4 inches. Weight: 0.2 Pounds.\"}]\"', 7.95, 'gadget', 'img2_6Z0P8C3Q.png', 'https://www.PopularToolZone.com/img/img17.png', 0, NULL, 0),
(2, '8Q5F1B7I', 'Pen Organizer', '\"[{\"title\":\"Easy organizing\",\"text\":\"Desktop pen and pencil holder helps keep office supplies neatly stored and easy to access.\"},{\"title\":\"Versatile\",\"text\":\"Tall, sturdy, and spacious enough to hold a variety of office supplies, including pens, pencils, rulers, scissors, and more.\"},{\"title\":\"Convenient\",\"text\":\"Helps create a neat, orderly work area at home, in the classroom, or at the office.\"},{\"title\":\"Elegant\",\"text\":\"Clean lines and a simple profile create an overall attractive appearance.\"},{\"text\":\"Dimensions: 3.31 x 3.35 x 3.31 inches; Weight: 0.24 Pounds; Material: Plastic; Color: White and Pink\"}]\"', 5.95, 'gadget', 'img2_8Q5F1B7I.png', 'https://www.ProTechDeviceLover.com/img/img1.png', 0, NULL, 0),
(5, '3Z5G5Q1R', 'Pen With Palm Rejection, Active Pencil', '\"[{\"text\":\"1.5mm pen tip can replace your finger to execute finer instructions, it easy to install and tear off the tips on your pen without any tool. No lag\\/offset\\/breaking point! Compared with the ordinary pen, it has higher sensitivity, more accurate signal, more comfortable hand. Not easy to break!\"},{\"text\":\"Dimensions: 57.48 x 2.48 x 0.67 inches; Weight: 2.39 ounces; Material: Indium Tin Oxide(ITO); Color: White\"}]\"', 39.99, 'gadget', 'img5_3Z5G5Q1R.png', 'https://www.LifeProTechTool.com/img/img4.png', 0, NULL, 0),
(2, '3R4Z8C3Y', 'Pen/Pencil Holder', '\"[{\"text\":\"It happens to everyone. You go to pull out your notebook, your journal, or your notepad and you don\\u2019t have a pen. Try your pockets. Try your bag. Try your bag pockets. You don\\u2019t have one. It\\u2019s time to ask a stranger.\"},{\"text\":\"Never lose your pen again! Pen Station attaches to any notebook and carries any pen. An ultra-thin, yet durable, plastic platform makes Pen Station invisible to the writing surface. A wide elastic loop secures your pen to the notebook so your pen passenger always arrives safe. Form follows function with Pen Station. The stealthy, angled design deflects other objects in your bag while also complementing the most sophisticated notebook.\"}]\"', 5.95, 'gadget', 'img2_3R4Z8C3Y.png', 'https://www.ProTechProMaster.com/img/img1.jpg', 0, NULL, 0),
