(5, '8C5Y3H2G', 'Baseball Bat', '\"[{\"text\":\"Gives you a finish you can feel in an incredibly durable top coat that not only drastically increases surface hardness, but gives you a mirror-like finish in high-gloss models, elevating the color and grain definition of the bat.\"},{\"text\":\"Made of bamboo and the strongest maple wood available, ensuring durability and consistent performance every at-bat.\"},{\"text\":\"Length: 33 inches; Weight: \\u200e0.94 pounds; Color: Wood\"}]\"', 39.99, 'sport', 'img5_8C5Y3H2G.png', 'https://www.SuperGreatSportDeal.com/img/img4.png', 0, NULL, 0),
(2, '2D6S3E8T', 'Baseball Bat Weight', '\"[{\"text\":\"BAT WEIGHT is a great bat accessory for youth and teen athletes to use for pre-game warm ups or while on deck.\"},{\"text\":\"VINYL COVERED, CAST IRON CONSTRUCTION for long-lasting use and durability.\"},{\"text\":\"FITS 2-1\\/4\\\" TO 2-3\\/4\\\" BARREL SIZES and provides a secure fit.\"},{\"text\":\"NOT LEGAL FOR USE in Little League baseball.\"},{\"text\":\"Dimensions: 7.92 x 5.67 x 1.58 inches. Weight: 0.52 Kilograms.\"}]\"', 6.25, 'sport', 'img2_2D6S3E8T.png', 'https://www.SuperSportVision.com/img/img15.png', 0, NULL, 0),
(6, '8U3Z8A0G', 'Baseball Display Case', '\"[{\"text\":\"The shelves in the display case are movable. Not only is it convenient to wipe and clean, but it\'s also easy to hold and put a baseball. No assembly required\\uff0ccomes in one piece.\"},{\"text\":\"Delicate gift for any occasion, such as Fathers\' Day, Birthday, Valentine\'s Day, Christmas, Thanksgiving, Graduation, Wedding, Anniversary.\"}]\"', 69.99, 'sport', 'img6_8U3Z8A0G.png', 'https://www.PowerfulSportsPro.com/img/img5.jpg', 0, NULL, 0),
(6, '7M6M7P3K', 'Baseball Rebounder Net', '\"[{\"text\":\"MULTI ANGLE RETURN: This pitch back is constructed with a multi angle frame to produce pop flies, grounders and line drives.\"},{\"text\":\"EASY ATTACH STRIKE ZONE: Work on mastering accuracy and pitching skills by attaching the self stick strike z1 at the desired height. No catcher needed, just rear back and hit your target.\"},{\"text\":\"ALL WEATHER STEEL CONSTRUCTION: Constructed with 3\\/4 inches weather resistant steel piping to ensure it can stand up to heavy use year after year.\"},{\"text\":\"HEAVY DUTY NET: The 24 ply PE net is designed to stay tight for a reliable consistent rebound session after session.\"},{\"text\":\"PERFECT FOR YOUTH BASEBALL: This rebounder is the perfect training tool for any Little Leaguer to practice throwing, pitching, fielding and more.\"},{\"text\":\"Dimensions: 21.54 x 12.8 x 3.35 inches. Weight: 2.95 Kilograms.\"}]\"', 69.99, 'sport', 'img6_7M6M7P3K.png', 'https://www.AllSportAllied.com/img/img6.png', 0, NULL, 0),
(6, '7U5J2R0J', 'Baseball/Softball/Slow Pitch Batting Tee', '\"[{\"text\":\"10 lb. heavyweight base with a commercial grade rubber over mold.\"},{\"text\":\"Heavy Base features three contact points for maximum balance, increased stability and less creep.\"},{\"text\":\"Engineered for optimal weight distribution and versatility.\"},{\"text\":\"Achieve any tee placement position on or off home plate without the tee leaning.\"},{\"text\":\"Solid steel frame built for long lasting durability.\"},{\"text\":\"Dimensions: 25.5 x 10 x 3.4 inches. Weight: 6.58 Kilograms.\"}]\"', 69.99, 'sport', 'img6_7U5J2R0J.png', 'https://www.SmartSportFan.com/img/img6.png', 0, NULL, 0),
(2, '4Z0W2F2F', 'Basketball Net', '\"[{\"text\":\"Indoor \\/ Outdoor Use. White polyester.\"},{\"text\":\"Heavy duty design for outdoor use.\"},{\"text\":\"Dimensions: 11.1 x 5.47 x 1.46 inches. Weight: 0.2 Kilograms.\"}]\"', 7.95, 'sport', 'img2_4Z0W2F2F.png', 'https://www.SportSmartZone.com/img/img17.png', 0, NULL, 0),
(6, '4F5L3G0J', 'Basketball Rim', '\"[{\"text\":\"This heavy duty rim is built to take a slam. It\'s made of 5\\/8\\\" solid steel with double compression springs for spring-back action. Also includes a all-weather nylon net and hardware.\"},{\"text\":\"Dimensions: 18 inches diameter; Weight: 2.47 ounces(net); Color: Black; Quantity: 1 Rim\"}]\"', 69.99, 'sport', 'img6_4F5L3G0J.png', 'https://www.TheSportisfyPro.com/img/img6.jpg', 0, NULL, 0),
(4, '5V5P6W5C', 'Bike Equipment Mat', '\"[{\"text\":\"High density mat helps protect your floors and carpets from the damage effects of heavy exercise equipment\\u2019s.\"},{\"text\":\"3-feet x 6.5-feet will be large enough for most treadmills, elliptical and other exercise equipment\\u2019s.\"},{\"text\":\"Absorbs vibration and reduces noise caused by all sorts of equipment while prolong the life of your equipments.\"},{\"text\":\"Will not bleed onto carpet and easy to clean with just a damp cloth. Pattern design to prevent machines from moving around.\"},{\"text\":\"Dimensions: 0.05 x 0.06 x 0.01 inches. Weight: 6.5 Pounds.\"}]\"', 24.99, 'sport', 'img4_5V5P6W5C.png', 'https://www.BestGymPro.com/img/img3.png', 0, NULL, 0);
