(4, '5O8T7Z6X', 'Gym Equipment Storage Rack', '\"[{\"text\":\"Our 8 prong organizer is a fantastic way to store gym equipment or other household items.\"},{\"text\":\"This workout gear storage rack is space efficient way to organize your home or gym.\"},{\"text\":\"Our black angled rack prong organizer is made from durable powder coated high quality steel that holds up to 30lbs.\"},{\"text\":\"Made of heavy - duty ABS plastic.\"},{\"text\":\"Closed box packaging.\"},{\"text\":\"Dimensions: 17.4 x 4.84 x 3.46 inches; Weight: 2.99 pounds; Color: Black\"}]\"', 24.99, 'sport', 'img4_5O8T7Z6X.png', 'https://www.MuscularSports.com/img/img3.jpg', 0, NULL, 0),
(6, '5K1M5R1J', 'Gym Training Bag', '\"[{\"text\":\"High-quality sneaker travel bag with dividers that can be placed individually to fit your needs. Can be used as sneaker bag for travel, big travel bag, duffle bag, shoe bag, gym\\/sports bag, durable bag, college bag.\"},{\"text\":\"Multifunctional: perfect for the gym, travel, picknick, baseball, football or basketball equipment, clothes.\"},{\"text\":\"With the dividers you can store your shoes, clothes or other essentials neatly. Take the dividers out if you need more space or attach them individually within the bag.\"},{\"text\":\"This large sneaker bag is the perfect asseccory for sneakerheads. It provides many small and large side pockets to store your laptop for example.\"},{\"text\":\"Dimensions: 22.25 x 15 x 4 inches; Weight: 2.1 pounds; Color: Black\"}]\"', 69.99, 'sport', 'img6_5K1M5R1J.png', 'https://www.TheAthletionGoods.com/img/img6.jpg', 0, NULL, 0),
(2, '1Z4R6M7K', 'Hand Expander', '\"[{\"text\":\"Finger Strengthening - Small size and light weight, you can do finger stretching exercise anytime and anywhere.\"},{\"text\":\"Made of Soft Silicone - Made of environmentally friendly silicone, soft, non-cracking, long service life and non-irritating to the skin.\"},{\"text\":\"Rehabilitation Exercise - Perfect for hand rehabilitation exercises, can also be used for sports exercise such as rock-climbing, archery, basketball, volleyball etc.\"},{\"text\":\"Exercise Muscles Extensor - Can exercises the extensor muscles located in the back of the forearm and palms muscles for increased hand control.\"},{\"text\":\"Fit For Varied Activities - Varied activities, including crushing, pinching and extensions work to improve the dexterity of both the fingers and hands.\"},{\"text\":\"Dimensions: 1 x 1 x 1 inches. Weight: 0.035 ounces.\"}]\"', 6.25, 'sport', 'img2_1Z4R6M7K.png', 'https://www.MuscleForSoul.com/img/img15.png', 0, NULL, 0),
(10, '6H0Y5C7W', 'Heart Rate Monitor', '\"[{\"text\":\"Are you looking for a simple and easy to use HRM that monitors your heart rate in real-time while workout? Our heart rate monitor with chest strap is exactly that one for you. It is an essential device for those who want to keep scientific sports and improve their fitness.\"},{\"text\":\"Continuous, accurate real-time heart rate provides clear workout guidance, and use manual heart rate target zones with audio and visual target zone alarm to find the right exercise intensity for your goals.\"},{\"text\":\"Intelligent calorie feature based your personal data provides more accurate calories burned calculation to keep your fitness training simple.\"},{\"text\":\"A large and clear display with big numbers make you read easy. Includes full watch functions with stopwatch. Comfortable fabric strap with transmitter and coded heart rate transmission to avoid cross-talk.\"}]\"', 141, 'sport', 'img10_6H0Y5C7W.png', 'https://www.ActiveInSports.com/img/img12.jpg', 0, NULL, 0);
