(9, '5S2H6U4Q', 'Aquarium Starter Kit', '\"[{\"text\":\"All the equipment needed to get started in one box.\"},{\"text\":\"Kit includes: glass aquarium, low profile LED hood, power filter, filter cartridge, submersible preset heater, premium fish food sample, water conditioner sample, fish net, thermometer and setup guide.\"},{\"text\":\"Preset heater will keep your aquarium temperature at a constant 78 degrees, appropriate for most tropical fish.\"},{\"text\":\"Low profile full hood contains vibrant cool white LED lighting to bring your aquatic environment to life.\"},{\"text\":\"Filter has a red LED light that flashes to indicate when it\\u2019s time to change the cartridge.\"},{\"text\":\"Simply add your favorite style of plants and decor (sold separately).\"},{\"text\":\"Dimensions: 27 x 20 x 16.5 inches. Weight: 32.9 pounds.\"}]\"', 132, 'pet', 'img9_5S2H6U4Q.png', 'https://www.SniffyPupAndFriends.com/img/img10.png', 0, NULL, 0),
(8, '4G7V7A8Z', 'Aquarium Stone Medium Size', '\"[{\"text\":\"Decorate your aquariums, fish tanks, ponds and water features with this decorative rock kit.\"},{\"text\":\"Dimensions: 20 x 10 x 8 inches; Weight: 44 pounds; Color: Gray\"}]\"', 106.58, 'pet', 'img8_4G7V7A8Z.png', 'https://www.AllTheBestPet.com/img/img7.jpg', 0, NULL, 0),
(5, '3S5S7T0Z', 'Aquarium Thermometer', '\"[{\"text\":\"High Accuracy: Temperature Range: -50\\u2103 ~ +70\\u2103 (-58 \\u00b0F ~ +158 \\u00b0F); This aquarium thermometer is accurate to +\\/- 1\\u2103, no matter whether the probe is placed in any corner of this fish tank, an accurate temperature will be displayed.\"},{\"text\":\"LCD Display: Comes with a large LCD display that is easy to read clearly and accurately.\"},{\"text\":\"Adjustable Unit Temperature: Easy to adjust the temp between Fahrenheit and Celsius displays; Long-press the power button to adjust the unit display.\"},{\"text\":\"Two Suction Cups: Equipped with two suction cups, one for attaching the cable to the side of the tank and another for attaching the thermometer.\"},{\"text\":\"Super Long Cable: The cable length connected to the probe is 78.74 inch (2 m) superior to 23.62 inch (60 cm) standard cable length; Allows accurate temperature measurements in even the largest fish tanks.\"},{\"text\":\"Dimensions: 5.04 x 4.21 x 1.14 inches; Weight: 1.41 Ounces.\"}]\"', 39.99, 'pet', 'img5_3S5S7T0Z.png', 'https://www.PetsEnjoyment.com/img/img4.png', 0, NULL, 0),
(3, '3R7B8U8U', 'Aquarium Water Bottle', '\"[{\"text\":\"Color: Clear Aquarium Mount. Material: Plastic, stainless steel. Dimensions: 9.3 x 1.9 x 0.1 inches.\"},{\"text\":\"The Perfect Size: The 8oz bottle is the perfect way to give you Hamster Mice and other small animal\\u2019s water inside their aquarium tank.\"}]\"', 19.99, 'pet', 'img3_3R7B8U8U.png', 'https://www.PetsParadiseStore.com/img/img2.png', 0, NULL, 0),
(8, '3O0Q5M5R', 'Arched Bird Cage', '\"[{\"text\":\"Includes large hinged door, stand w\\/rolling casters, pullout tray, S.S cups & wooden perch.\"},{\"text\":\"This spacious cage is a roomy enough for Lovebirds, Cockatiels & Conures.\"}]\"', 106.58, 'pet', 'img8_3O0Q5M5R.png', 'https://www.AnimalFairPetStore.com/img/img7.jpg', 0, NULL, 0),
(4, '4A0M3J2Z', 'Artificial Grass Puppy Pad', '\"[{\"text\":\"Dog Grass Pad With Tray \\u2013 This artificial turf dog mat provides a sanitary place for your pet to relieve themselves inside or on your balcony or patio. It\\u2019s a reusable alternative to disposable dog pads for small to medium-sized pets.\"},{\"text\":\"Easy Puppy Training \\u2013 Potty training your new best friend just got easier, even if you live in an apartment or work long hours. This dog litter box helps avoid accidents while enforcing your pup\\u2019s training that they should go potty on grass.\"},{\"text\":\"Three-Layer System \\u2013 The artificial grass mat sits on top of a secure plastic drainage insert that allows liquid to empty into the durable collection tray.\"},{\"text\":\"Easy Clean-Up \\u2013 Cleaning this reusable turf grass bathroom mat is quick and easy. Rinse the three layers with soapy water and clean-up is complete.\"},{\"text\":\"Product Details \\u2013 Materials: Synthetic Plastic (Grass Mat) and Hard Plastic (Tray and Insert). Dimensions: (L) 25\\u201d x (W) 20\\u201d x (H) 1.25\\u201d. Weight: 4.1 Pounds.\"}]\"', 24.99, 'pet', 'img4_4A0M3J2Z.png', 'https://www.LittlePetGathering.com/img/img3.png', 0, NULL, 0),
(8, '6A4U1V7V', 'Automatic Ball Launcher', '\"[{\"text\":\"Safety: Multiple sensors keep you and your dog safe; a Front Motion Sensor protects pets and people standing up to 7 feet in front of the launch area and reinforces safely loading balls into the side pocket.\"},{\"text\":\"Distance: Launcher throws between 8-30 feet; has 9 distance settings and 6 angle settings; pull out and twist angle knob and distance knob to adjust the launcher\\u2019s range; Turn knobs left for max distance.\"},{\"text\":\"Pause: The Automatic Ball Launcher features a 15 minute rest interval after 15 minutes of play; the rest period ensures dogs aren\\u2019t playing past the point of exhaustion.\"},{\"text\":\"Flexibility: The PetSafe Automatic Ball Launcher works best with standard size tennis balls 2 5\\/8\\u201d in diameter but can be used with smaller tennis-type balls; up to 3 balls can be loaded at a time.\"}]\"', 109, 'pet', 'img8_6A4U1V7V.png', 'https://www.PetsParadiseStore.com/img/img8.png', 0, NULL, 0),
(9, '5F2U2E2J', 'Automatic Ball Launcher for Dogs', '\"[{\"text\":\"The automatic dog ball launcher is an automatic,interactive fetch system.The interactive dog toys keep your pet happy while giving your arm a break.\"},{\"text\":\"Dimensions: 11.5 x 11.1 x 9.1 inches; Weight: 4.2 pounds; Color: White\\/Green; Quantity: 1 Ball Launcher\"}]\"', 129.87, 'pet', 'img9_5F2U2E2J.png', 'https://www.ThePapperPet.com/img/img13.jpg', 0, NULL, 0),
(9, '5N7Q2J0N', 'Automatic Cat Feeder', '\"[{\"text\":\"Schedule Your Pet\'S Meals: Conveniently schedule up to 12 meals per day for your cat or dog; best for cats and small to medium sized dogs.\"},{\"text\":\"Flexible Portions: Schedule 1\\/8 cup to 4 cups per meal depending on your pet\\u2019s unique needs; requires 4 D-cell alkaline batteries (not included) or power adaptor (adaptor sold separately).\"},{\"text\":\"Works With Most Kibble: Feeder works with dry and semi-moist pet kibble that is up to 3\\/4 inch in diameter.\"},{\"text\":\"Slow Feed Options: For pets who tend to eat too quickly, schedule meals larger than 1\\/8 cup to slowly dispense over 15 minutes.\"},{\"text\":\"Pet-Proof: The feeder is designed to keep prying paws from sneaking food before scheduled mealtimes.\"},{\"text\":\"Easy To Clean: Place the BPA-free plastic feeder parts and stainless-steel bowl in the top-rack of the dishwasher for easy cleaning; hand wash the conveyor system separately.\"}]\"', 132, 'pet', 'img9_5N7Q2J0N.png', 'https://www.CutiesPawNation.com/img/img10.png', 0, NULL, 0),
(9, '6N0D6L4E', 'Automatic Cat Feeders', '\"[{\"text\":\"Programmable Timed Feeding - No longer woken up by pets in the morning, no need to worry about pet feeding when going out! Automatic Cat Feeders can provide the timed and quantitative meals for your pets, 1-4 meals a day, 1-40 portions per meal (5 mg per portion). Start customizing a healthy diet for your cats and dogs now!\"},{\"text\":\"2-Way Splitter & 304 Stainless Steel Body - Perfect suitable for multi-pet family. This 4.5L cat feeder is equipped with 2-way splitters, baffles, anti-clog design structure, which ensure dispensed evenly between the 2 bowls. The pet food storage container and 2 bowls are made of 304 stainless steel, which can provide the more healthy and hygienic meal for your pets.\"},{\"text\":\"Dual Power Supply Design - Even if power outages, no worry about the cat food dispenser not supplying food. This Automatic Cat Feeders is powered by 5V DC adapter and\\/or batteries. Even if the pet feeder power outages, 3 alkaline D-type batteries will keep the equipment running normally. In addition, it equipped with setting memory, you don\'t need to reset the parameters after power off.\"},{\"text\":\"10s Voice Recorder - You can choose to call your pet before meals! Long press the \\\"Mic\\\" button of auto cat feeder to record 10s voice, calling your pet regularly and let your voice accompany them every meal. You can also choose to cancel the recording, no voice will be played before the meal.\"},{\"text\":\"Easy & Friendly Operation - Easy installation\\/disassembly + clear programming allow you to get started in 5 minutes.\"},{\"text\":\"Dimensions: 14.68 x 7.64 x 7.64 inches; Weight: 3.9 Pounds.\"}]\"', 139, 'pet', 'img9_6N0D6L4E.png', 'https://www.CutiePiePetStore.com/img/img11.png', 0, NULL, 0),
(9, '2Z6Q1B6Q', 'Automatic Dog Ball Launcher', '\"[{\"text\":\"The automatic ball throwing machine is designed especially for small to medium dogs, it\\u2019s a perfect toy for your dog to play even when you\\u2019re away! All you have to do is plug it in, choose your launching distance and then drop in a mini ball. Soon your dog will learn how to drop it himself, which stimulates his mind and allows for totally independent play.\"},{\"text\":\"Dimensions: 12.13 x 11.37 x 9.76 inches; Weight: 4.76 pounds; Material: ABS; Color: White and Yellow\"}]\"', 124.53, 'pet', 'img9_2Z6Q1B6Q.png', 'https://www.BestfriendPetStorePro.com/img/img14.png', 0, NULL, 0),
(10, '5H1D0R0P', 'Automatic Dog Toy', '\"[{\"text\":\"Safety: Multiple sensors keep you and your dog safe; a front motion sensor protects pets and people standing up to 7 feet in front of the launch area and reinforces safely loading balls into the side pocket.\"},{\"text\":\"Distance: Launcher throws between 8-30 feet; has 9 distance settings and 6 angle settings; pull out and twist angle knob and distance knob to adjust the launcher\\u2019s range; TURN KNOBS LEFT FOR MAX DISTANCE.\"},{\"text\":\"Pause: The Automatic Ball Launcher features a 15 minute rest interval after 15 minutes of play; the rest period ensures dogs aren\\u2019t playing past the point of exhaustion.\"},{\"text\":\"Options: Automatic Ball Launcher can be used both indoors and outside to entertain and exercise your dogs; begin use outdoors as an introduction; use the included power cord or 6 D cell batteries (not included); water-resistant.\"},{\"text\":\"Flexibility: Automatic Ball Launcher works best with standard size tennis balls 2 5\\/8\\u201d in diameter but can be used with smaller tennis-type balls; up to 3 balls can be loaded at a time.\"},{\"text\":\"Training: Audible tones alert you and your dog so you know when the ball is detected and will launch, when 2 second motion detection delay is activated and when 15 minute rest time begins and ends.\"}]\"', 141, 'pet', 'img10_5H1D0R0P.png', 'https://www.CutiesPawNation.com/img/img12.png', 0, NULL, 0),
(7, '3G7N7I1Y', 'Automatic Fish Feeder', '\"[{\"text\":\"Manual and Automatic Mode: Automatic Fish Feeder allows you to feed at any time. Aquarium auto fish food dispenser can be easily set up regularly. You can set 8h, 12h and 24h for feeding, just gently press the button on the setting page. At the same time, you can also feed manually at any time. The fish tank feeder can free up your time and let you spend a good holiday easily.\"},{\"text\":\"Adjustable Feeding Amount: The Automatic Fish Feeder uses an open design. You can add fish food without removing the timer feeder. You only need to gently push the opening at the top to complete. And you can decide how much fish food you want to put in according to the number and size of the fish, and the feeder will automatically rotate 360\\u00b0for feeding at the time you set.\"},{\"text\":\"Battery Powered & Moisture-Proof Design: The Electric feeder is powered by two AA batteries, which is easy to replace and can be used continuously for 2-3 months. The feeder adopts moisture-proof design to ensure the freshness of food. No blockage will occur. The feeder is suitable for fish food such as strips, powder , granules, and flakes.\"},{\"text\":\"0.05 Gal & Two Fixed Methods: The Electric Adjustable Auto Fish Food Dispenser has a capacity of 200ml, which can hold a variety of types of food, and the Timer feeder adopts two fixed methods and is suitable for a variety of aquariums. One is to fix the bracket on the wall of the aquarium. The second is to stick it on the aquarium with double-sided stickers. Both methods can make the feeder firmly fixed on the aquarium.\"}]\"', 89.99, 'pet', 'img7_3G7N7I1Y.png', 'https://www.ThePetHappniess.com/img/img6.png', 0, NULL, 0),
(3, '1Q5J8H3T', 'Automatic Fish Feeder for Aquarium', '\"[{\"text\":\"CUSTOM MODE allows you to set up to SIX automatic feedings per day. You can program it to feed at any hour of the day or night. If you think programming is a complicated thing , then the 6 AUTO MODES are a good choice for you. At the same time, you can also feed manually and experience the fun of feeding with MANUAL MODE.\"},{\"text\":\"Our fish vacation feeder is designed with 2 install methods to meet different needs. One is to fix the feeder to the tank wall by using the holder. Another is to push the feeder\\u2019s bracket into the base\\u2019s track, and place it on a flat surface.\"},{\"text\":\"Works with most types of fish food, such as dry, loose flake and particle feed, flakes, pellets, powders and small strips. Ideal for various sizes of aquariums, fish tanks. Not only for goldfish, it also can be used to feed most of pet fishes, turtles, newts and frogs.\"}]\"', 19.99, 'pet', 'img3_1Q5J8H3T.png', 'https://www.AnimalFairPetStore.com/img/img2.jpg', 0, NULL, 0),
(5, '7R7P1J7U', 'Automatic Horse Waterer', '\"[{\"text\":\"Perfect For Livestock: Provide a constant source of fresh water for your horses, cattle, sheep, hogs, and other livestock.\"},{\"text\":\"Automatic Design: Automatic watering trough for agricultural and farmhouse use.\"},{\"text\":\"Durable Steel Frame: Crafted from rustproof heavy-duty, prime-quality drawn galvanized steel; Dimensions (L x W x H): 10 x 10.75 x 5.5 inches.\"},{\"text\":\"Connects To Most Water Sources: 1\\/2-inch connection is easy to attach to your outdoor plumbing, garden hose, or flex hose.\"},{\"text\":\"Safety First: Safe to operate with pressures ranging from 20 to 50 psi.\"},{\"text\":\"Weight: 4 pounds.\"}]\"', 49.95, 'pet', 'img5_7R7P1J7U.png', 'https://www.LovelyPetDistrict.com/img/img5.png', 0, NULL, 0),
(10, '2E0I0E1H', 'Automatic Pet Feeder', '\"[{\"text\":\"SMARTPHONE NOTIFICATIONS: The pet feeder will send a notification to your smartphone when the feeder is running low or empty, or if there is an error with the feeder.\"},{\"text\":\"GREAT FOR PETS WHO EAT TOO QUICKLY: Slow Feed setting dispenses meals larger than 1\\/8 cup in small amounts over 15 minutes.\"},{\"text\":\"FLEXIBLE PORTIONS: Schedule up to 12 meals daily that can be dispensed from 1\\/8 cup to 4 cups to fit your pet\'s needs; If you need to temporarily stop the feeder from dispensing you can turn off the schedule in the app.\"},{\"text\":\"TOP-RACK DISHWASHER SAFE: Lid, hopper, bowl and bowl holder are top-rack dishwasher-safe for convenient cleaning.\"},{\"text\":\"Dimensions: 16 x 13 x 10 inches. Weight: 8.2 Pounds.\"}]\"', 141, 'pet', 'img10_2E0I0E1H.png', 'https://www.PamperPetHouse.com/img/img12.png', 0, NULL, 0),
(8, '1L2P2A0K', 'Automatic Pond Fish Feeder', '\"[{\"text\":\"The food container of the feeder is large enough to hold 5 pounds of fish food at most, the max capacity is 5.5L. With this large pond fish feeder, you can spend a long vacation without worrying nobody help feed your fish. You don\'t need to frequently add new fish food. The container is translucent, you can easy to see how much fish food is inside it.\"},{\"text\":\"Target Species: Fish; Package Dimensions: 15.51 x 12.52 x 7.8 inches; Weight: \\u200e5 Pounds; Operation Mode; Automatic; Material: Plastic; Color: Black\"}]\"', 115.89, 'pet', 'img8_1L2P2A0K.png', 'https://www.PetFriendSupply.com/img/img9.png', 0, NULL, 0),
(9, '0V5U5F7Y', 'Automatic Self-Cleaning Cat Litter Boxes', '\"[{\"text\":\"Strike scooping the litter box off your to do list. With the Self-Cleaning Litter Box, there\\u2019s no scooping, cleaning or refilling your cat\\u2019s litter box for weeks. It uses the crystal litter which provides 5 times better odor control than traditional clumping litter. Crystal litter absorbs urine and dehydrates solid waste. This low tracking litter also helps keep your floors clean because it is 99 percent dust free and doesn\\u2019t stick to your cat\\u2019s paws. Safety sensors ensure that the scooping cycle only occurs when your cat is out of the box. It uses disposable trays that have a plastic lining to help protect against leaking, and each tray comes with a lid for quick and easy disposal. Just remove the tray and throw it away. Here to help you and your pet live happy together.\"},{\"text\":\"Dimensions: 27.37 x 19 x 7 inches; Weight: 10.9 Pounds; Material: Crystal; Color: Purple\"}]\"', 132, 'pet', 'img9_0V5U5F7Y.png', 'https://www.wonderfulpetzone.com/img/img10.png', 0, NULL, 0),
(5, '1O3B7K2Q', 'Ball for Horse', '\"[{\"text\":\"This Mega Ball measures 30 inches in diameter, and ships with a hand pump.\"},{\"text\":\"Mega Ball is a heavy duty inflatable ball that is made specifically for horses.\"},{\"text\":\"Mega Balls can be used for enjoyment or training and are favorites of Natural Horsemanship practitioners.\"},{\"text\":\"Perfect for field or pasture time, Mega Balls provide horse of exercise and entertainment for your horse.\"},{\"text\":\"Dimensions: 9.2 x 8 x 4.7 inches. Weight: 2.5 pounds.\"}]\"', 39.99, 'pet', 'img5_1O3B7K2Q.png', 'https://www.AmazingBarkMeow.com/img/img4.png', 0, NULL, 0),
(5, '6A8R4R8C', 'Bark Collar for Dogs', '\"[{\"text\":\"Bark collar for small medium large dogs with 7 sensitivity levels.\"},{\"text\":\"Dimensions: \\t2.76 x 1.46 x 1.57 inches; Weight: 6.4 ounces; Color: Black;\"}]\"', 39.99, 'pet', 'img5_6A8R4R8C.png', 'https://www.HappyPetsPetStore.com/img/img4.jpg', 0, NULL, 0),
(8, '5A8L0V1M', 'Bark Control Collar', '\"[{\"text\":\"This bark collar detects barking by using our  technology that quickly determines your dog\'s unique barking style and filters out all the rest to avoid any false corrections.\"},{\"text\":\"With 3 selectable modes of operation (Temperament Learning, Progressive Correction, and User-Selected) and 10 levels of static stimulation you can choose the type of bark control that works best for your dog\'s temperament.\"},{\"text\":\"The SBC-R is waterproof and submersible to 10 feet, fitting dogs 8 pounds or larger with neck sizes 5\\\" - 22\\\".\"},{\"text\":\"Rechargeable lithium-ion batteries charge in 2 hours and last 200 hours per charge; It also features a low battery indicator so you know when it is time to charge.\"},{\"text\":\"The detailed operating guide and a locally based Customer Care Center, available 6 days a week, provide support when you need it. Automatic Shut Off : Yes. Correction stops after 80 seconds.\"}]\"', 109, 'pet', 'img8_5A8L0V1M.png', 'https://www.ThePetHappniess.com/img/img8.png', 0, NULL, 0),
(5, '3J7R6R5Z', 'Baseball Display Case', '\"[{\"text\":\"The shelves in the display case are movable. Not only is it convenient to wipe and clean, but it\'s also easy to hold and put a baseball. No assembly required\\uff0ccomes in one piece.\"},{\"text\":\"Delicate gift for any occasion, such as Fathers\' Day, Birthday, Valentine\'s Day, Christmas, Thanksgiving, Graduation, Wedding, Anniversary.\"}]\"', 49.95, 'pet', 'img5_3J7R6R5Z.png', 'https://www.CoolBodyGym.com/img/img5.jpg', 0, NULL, 0),
(2, '2Q3W3B3W', 'Basketball Net', '\"[{\"text\":\"Indoor \\/ Outdoor Use. White polyester.\"},{\"text\":\"Heavy duty design for outdoor use.\"},{\"text\":\"Dimensions: 11.1 x 5.47 x 1.46 inches. Weight: 0.2 Kilograms.\"}]\"', 7.95, 'pet', 'img2_2Q3W3B3W.png', 'https://www.CoolBodyGym.com/img/img17.png', 0, NULL, 0),
(10, '4Y7V4D6U', 'Big Dog Bed', '\"[{\"text\":\"360-degree zipper easy to assemble and to clean.\"},{\"text\":\"Cover is machine washable\\/dryer friendly.\"},{\"text\":\"Water proof pillow liner.\"},{\"text\":\"Reversible (can be used in two different sides).\"},{\"text\":\"Long lasting, extra plush fabric.\"},{\"text\":\"Dimensions: 30 x 30 x 30 inches. Weight: 6.4 Pounds.\"}]\"', 141, 'pet', 'img10_4Y7V4D6U.png', 'https://www.SniffyPupAndFriends.com/img/img12.png', 0, NULL, 0),
(8, '6J3Y5I8G', 'Bike Pet Carrier', '\"[{\"title\":\"Strong\",\"text\":\"Travel Bike Basket is the perfect solution to bicycling with your dog and holds up to 20 lbs.\"},{\"title\":\"All Inclusive\",\"text\":\"Includes basket and front bicycle mount (hood and rear bike mount are also available separately).\"},{\"title\":\"Storage\",\"text\":\"Zipper pocket for storing leashes and treats, mesh pocket for holding water bottles.\"},{\"title\":\"Security\",\"text\":\"Dual leash design inside the basket keeps your pet safe and secure in the bike basket.\"},{\"title\":\"Washable\",\"text\":\"Removable fleece liner in the basket is machine washable for easy care.\"}]\"', 109, 'pet', 'img8_6J3Y5I8G.png', 'https://www.YourBestFriendPetStore.com/img/img7.png', 0, NULL, 0),
(4, '2A3C8J2Z', 'Bin & Rake Pooper Scooper', '\"[{\"text\":\"The extended reach lightweight handle is ergonomically designed for easy pick-up and at 32 inches in height, eliminates the need to bend, kneel, or squat down to pick up pet waste.\"},{\"text\":\"Dimensions: \\t9 x 7 x 32 inches; Weight: 1.7 pounds; Color: Black\"}]\"', 24.99, 'pet', 'img4_2A3C8J2Z.png', 'https://www.HappyPetsPetStore.com/img/img3.jpg', 0, NULL, 0),
(9, '6D3F6W1U', 'Bird Bath', '\"[{\"text\":\"The base is cast iron, providing excellent stability.\"},{\"text\":\"The bird bath basin is cast aluminum with an antique pewter finish.\"},{\"text\":\"Hummingbirds and a flowering vine are cast into the design of the base, pedestal and basin plus cast metal bird figures are provided that mount to the edge of the basin.\"},{\"text\":\"Easy to assemble with tools provided.\"},{\"text\":\"Hummingbird design birdbath is 17\\\" in diameter x 32\\\" high and weighs about 35 lbs.\"},{\"text\":\"Dimensions: 19 x 19 x 29 inches. Weight: 33 pounds.\"}]\"', 139, 'pet', 'img9_6D3F6W1U.png', 'https://www.SuperPowerPaws.com/img/img11.png', 0, NULL, 0),
(8, '2A3N0G1C', 'Bird Cage', '\"[{\"text\":\"2 feeder doors, 2 cups, 2 perches.\"},{\"text\":\"Easy to clean removable metal grate and slide out bottom plastic tray.\"},{\"text\":\"It has a large swing out door with a door pin and small lift up doors.\"},{\"text\":\"Dimensions: 32.28 x 25.28 x 6.85 inches. Weight: 10 pounds.\"}]\"', 119, 'pet', 'img8_2A3N0G1C.png', 'https://www.SnugglePawsPro.com/img/img9.png', 0, NULL, 0),
(10, '4A0P1D0N', 'Bird Cage Cylindrical Wooden Nest', '\"[{\"text\":\"This house ornament made of premium Eco-friendly material, non-toxic, pollution-free, completely safe for life.\"},{\"text\":\"Dimensions: 6.69 x 4.72 inches; Weight: 1.25 pounds; Color: Wood\"}]\"', 141, 'pet', 'img10_4A0P1D0N.png', 'https://www.FluffyPetStorePro.com/img/img12.jpg', 0, NULL, 0),
(2, '8W8F0B7U', 'Bird Hammock', '\"[{\"text\":\"Easy and portable to hang with the bilateral hooks. Thick plush fabrics provide the good place for your parrots.\"},{\"text\":\"Strengthened base to keep the hammock in shape. Thick plush fabrics provide the good place for the pets.\"},{\"text\":\"Dimensions: 0.1 x 3.7 x 5.1 inches. Weight: 3.2 Ounces.\"}]\"', 7.95, 'pet', 'img2_8W8F0B7U.png', 'https://www.ThePerfectPetDepot.com/img/img17.png', 0, NULL, 0),
(2, '7F0J6V0C', 'Bird Ladder', '\"[{\"text\":\"Tested for safety and health. This is step ladder that adds a classic feel to your bird\'s cage.\"},{\"text\":\"Includes metal hooks that make it easy to hang this ladder on all wired bird cages.\"},{\"text\":\"Material: wood, metal.\"},{\"text\":\"Dimensions: 5 x 2 x 4 inches. Weight: 9.6 Ounces.\"}]\"', 6.25, 'pet', 'img2_7F0J6V0C.png', 'https://www.ThePerfectPetDepot.com/img/img15.png', 0, NULL, 0);
