(8, '3N7Y3V8K', 'Collar', '\"[{\"text\":\"For Stubborn Or Hard To Train Dogs: 4 adjustable levels of static correction; highest intensity available.\"},{\"text\":\"Tone And Vibration Warning: ideal for training hearing-impaired dogs; Battery: 9V alkaline battery.\"},{\"text\":\"Waterproof Receiver Collar: with low battery indicator.\"},{\"text\":\"Add Unlimited Pets To Your System: keep as many pets as you need safely in your yard with this add-a-dog receiver collar.\"},{\"text\":\"Adjustable Collar: fits pets 8 lb. or more with neck sizes 6 to 28 inches.\"},{\"text\":\"Long Battery Life: The collar uses a 9 Volt battery (not included).\"}]\"', 119, 'pet', 'img8_3N7Y3V8K.png', 'https://www.CutiesPawNation.com/img/img9.png', 0, NULL, 0),
(2, '3R6D5J2K', 'Compressed Catnip Ball for Cats', '\"[{\"text\":\"CATNIP WITHOUT THE MESS: Durable compressed catnip is less messy than lose catnip but still potent as can be.\"},{\"text\":\"STIMULATING CAT TOYS: Attract cats with catnip to engage them to frolic, romp & play.\"},{\"text\":\"DESIGNED FOR ACTIVITY: Ball shape your cat will chase with every bat.\"},{\"text\":\"TESTED FOR SAFETY: We always makes safety a priority, which is why we design products to meet child safety standards. They do not contain small parts, long cords, or other hazards, ensuring kids in the home are safe, whether they are two-footed or four-pawed.\"},{\"text\":\"Age range description: all life stages.\"},{\"text\":\"Dimensions: 2.25 x 5 x 4 inches. Weight: 1.28 Ounces.\"}]\"', 7.95, 'pet', 'img2_3R6D5J2K.png', 'https://www.MyPetSuperStar.com/img/img17.png', 0, NULL, 0),
