(10, '7A3U7Q0X', 'Electric Dog Fence', '\"[{\"text\":\"The system includes 650 Ft boundary wire to contain up to 3\\/4 acre of land,can be expanded to more land with the purchase of additional wires. You can lay the wire in any shape on the ground or underground, to keep your dog stay where you want it.\"},{\"text\":\"Product Dimensions: 12.2 x 8.15 x 2.09 inches; Weight: 2.38 Pounds; Batteries: 1 Lithium ion battery; Color: Black\"}]\"', 141, 'pet', 'img10_7A3U7Q0X.png', 'https://www.CharmingPetStore.com/img/img12.png', 0, NULL, 0),
(8, '3R8M4E8T', 'Electric Heated Pad/Warmer Bed for Pet & Reptile with/Without Temperature Control Options', '\"[{\"text\":\"Pet Heat Mat with Self-Regulated Temperature Control maintains the heat at 37 degrees above ambient temperature. Your animals get uniform warmth from the heated pad without fear of overheating.\"},{\"text\":\"Dimensions: 18 x 18 x 2 inches; Weight: 4.8 pounds; Color: Red\"}]\"', 109, 'pet', 'img8_3R8M4E8T.png', 'https://www.AllTheBestPet.com/img/img8.jpg', 0, NULL, 0),
(5, '4G3G4M1M', 'Electric Nail Clipper', '\"[{\"text\":\"Our Nail Trimmer is equipped with an ultra-quiet but powerful motor. It gently removes thin layers of the nail with minimal noise (under 50dB) and vibration so that sensitive pets won\'t be anxious or scared.\"},{\"text\":\"Battery life: 7 hours; Charging time: 1.5 hours; Material: ABS Plastic; Color: WHite\"}]\"', 49.95, 'pet', 'img5_4G3G4M1M.png', 'https://www.BestPetsWonderland.com/img/img5.png', 0, NULL, 0),
(8, '7K6B4N7Z', 'Electric Sheep Clippers', '\"[{\"text\":\"High Performance: The electric sheep shears has a 1000W powerful external motor, the speed can up to 2800r\\/min and meet different kinds of needs.\"},{\"text\":\"Premium Blades: Equipped with a 9-tooth blade and a 13-tooth blade for easy and quick shearing, sharp and wear-resistant. The tightness of the blade can be adjusted according to different wool.\"},{\"text\":\"Reasonable Design: External motor, good heat dissipation, easy to maintain. User-friendly design for comfortable gripping.\"},{\"text\":\"Simple Operation: This wool shearing machine is easy to use with one-button power switch, equipped with a 2m long cable, convenient for long-distance operation.\"},{\"text\":\"Widely Use: Our electric sheep clipper can be used for shearing various animal\'s fur, such as sheep, goats, alpacas, cattle and horses and so on.\"}]\"', 119, 'pet', 'img8_7K6B4N7Z.png', 'https://www.LovelyPetTown.com/img/img9.png', 0, NULL, 0),
(8, '1W1N4A3T', 'Electronic Shooting Earmuff', '\"[{\"text\":\"At the range or on the hunt, the Electronic Shooting Earmuff keeps you protected from hazardous noise and connected to your environment. It carries a Noise Reduction Rating (NRR) of 22dB. When the electronic ear protection is switched on, it employs built-in directional microphones that amplify ambient sounds to a safe 82dB. This allows you to hear important commands on the range and crucial environment sounds when hunting. For additional shooting hearing protection, it also actively listens and automatically stops amplification when loud impulsive sounds like firearm discharges exceed 82dB. Air Flow Control technology allows for a slim earcup design that ensures your firearm stock has clearance while shooting. The padded headband features telescopic height adjustment for a comfortable, customized fit. An external audio jack lets you connect the shooting earmuff to music device or scanner or another audio source. The battery delivers approximately 350 hours of usage, and the automatic shut-off feature engages after 4 hours to help save battery life.\"}]\"', 109, 'pet', 'img8_1W1N4A3T.png', 'https://www.CoolBodyGym.com/img/img8.jpg', 0, NULL, 0),
(8, '5S5E4W4J', 'Elevated Dog Cot', '\"[{\"text\":\"Size: 48. Material: Polyester, Alloy Steel, Metal, Oxford, Fabric. Color: Brown. Shape: Round.\"}]\"', 109, 'pet', 'img8_5S5E4W4J.png', 'https://www.AwesomePetShop.com/img/img8.png', 0, NULL, 0),
(5, '4Z5A8Y2A', 'Elevated Dog beds Waterproof', '\"[{\"text\":\"Raised dog bed can keep your dog off the ground to prevent from coldness of the floor, dampness of the carpet in winter and sunstroke in summer when they rest on the ground outdoors. And the elevated design also avoids the bites from bugs when dogs rest on the lawn. So the bed is really useful in each season. Also, lying on the soft cloth instead of hard floor protects dogs\' joints.\"},{\"text\":\"Dimensions: \\t36 x 30 x 7 inches; Weight: 6.77 pounds; Color: Black\"}]\"', 49.95, 'pet', 'img5_4Z5A8Y2A.png', 'https://www.EnjoyWithPets.com/img/img5.jpg', 0, NULL, 0),
(9, '8S7I2I3J', 'Equine Leather Skid Boot', '\"[{\"text\":\"Heavy Leather.\"},{\"text\":\"Maximum Protection.\"},{\"text\":\"Fitted Neolite Skid Cup.\"},{\"text\":\"Abrasion Resistant.\"}]\"', 124.53, 'pet', 'img9_8S7I2I3J.png', 'https://www.PetsParadiseStore.com/img/img12.png', 0, NULL, 0),
(8, '8X2D7L0B', 'Equipment Storage Organizer', '\"[{\"text\":\"Keep garage floors clear and utility rooms tidy with our Heavy-Duty Storage Bench. As a storage bin, it\\u2019s ready to fill with sneakers, balls , sports gear or outdoor and camping equipment. As a bench, it\\u2019s a no-fuss place to sit down and take off dirty shoes so floors stay clean.\"}]\"', 119, 'pet', 'img8_8X2D7L0B.png', 'https://www.CoolBodyGym.com/img/img9.jpg', 0, NULL, 0),
(4, '1K1L8J1J', 'Ergonomic Comb', '\"[{\"text\":\"These top-quality combs are a must-have for any professional or amateur groomer!\"},{\"text\":\"Features 40 tightly spaced \\u00bd\\\" stainless steel pins to rid hair of debris from tear stains, food, and dirt.\"},{\"text\":\"Ergonomically designed handle offers maximum comfort and control.\"},{\"text\":\"Stainless steel teeth glide effortlessly through coats.\"},{\"text\":\"Plastic handle has a hole, allowing the user to conveniently hang within reach.\"},{\"text\":\"Dimensions: 9 x 3.8 x 0.8 inches. Weight: 1.59 Ounces.\"}]\"', 24.99, 'pet', 'img4_1K1L8J1J.png', 'https://www.TheCharmingPet.com/img/img3.png', 0, NULL, 0),
(2, '2T7L3S6I', 'Ergonomic Comb for Grooming Dogs', '\"[{\"text\":\"These top-quality combs are a must-have for any professional or amateur groomer!\"},{\"text\":\"Features 40 tightly spaced \\u00bd\\\" stainless steel pins to rid hair of debris from tear stains, food, and dirt.\"},{\"text\":\"Ergonomically designed handle offers maximum comfort and control.\"},{\"text\":\"Stainless steel teeth glide effortlessly through coats. Plastic handle has a hole, allowing the user to conveniently hang within reach.\"},{\"text\":\"Dimensions: 9 x 3.8 x 0.8 inches. Weight: 1.58 ounces.\"}]\"', 7.95, 'pet', 'img2_2T7L3S6I.png', 'https://www.BestiePetCompanies.com/img/img17.png', 0, NULL, 0),
(2, '7C0R6K4K', 'Exercise Ball', '\"[{\"text\":\"Exercise Ball provides a safe, mobile environment that lets small pets get the exercise they need.\"},{\"text\":\"Simply place your pet inside the Exercise Ball for safe, interactive and healthy activity, and be sure to monitor your pet closely.\"},{\"text\":\"Made from non-toxic, non-breakable, clear plastic and is well-ventilated to ensure continuous air circulation.\"},{\"text\":\"Cleaning is easy, just use warm water and a mild detergent and rinse thoroughly.\"},{\"text\":\"Small size measures 4.7 inches in diameter and is suitable for dwarf hamsters and mice.\"},{\"text\":\"Dimensions: 5.2 x 5.2 x 5.43 inches. Weight: 0.16 Pounds.\"}]\"', 7.95, 'pet', 'img2_7C0R6K4K.png', 'https://www.FourLegsRegistry.com/img/img17.png', 0, NULL, 0),
(2, '7W1N8E1Q', 'Exercise Balls for Small Animals', '\"[{\"text\":\"GOING FOR A WALK: Perfect accessory to give your small pets exercise. Runner balls are a great way to play with your pet and gives them a safe way to burn off excess energy. They also provide a convenient place to safely place your pet while cleaning their cage.\"},{\"text\":\"EASY TO CLEAN: Along with the side access door ball can be disassembled in the center for easy cleaning.\"},{\"text\":\"QUALITY CONSTRUCTION: Balls are made in Belgium using quality materials.\"},{\"text\":\"Age range description: all life stages.\"},{\"text\":\"Dimensions: 7.09 x 7.09 x 7.09 inches. Weight: 7.1 Ounces.\"}]\"', 6.95, 'pet', 'img2_7W1N8E1Q.png', 'https://www.LovelyPetDistrict.com/img/img16.png', 0, NULL, 0),
(5, '5D4L2Z7C', 'Expandable Pet Carrier', '\"[{\"text\":\"Dont you think your pet deserves the same kind of love and care in an airplane that it gets from you at home. With the Expandable Pet Carrier your pet can now travel safely while lying down with utmost comfort and safety throughout the journey. Expertly crafted roof ventilation design allows for air circulation, keeping your pet from overheating and providing fresh supply of oxygen at all times. Easily stow away under the cabin seat.\"},{\"text\":\"Dimensions: 17.9 x 14 x 1.6 inches; Weight: 14 Pounds; Material: Mesh; Color: Black\"}]\"', 49.95, 'pet', 'img5_5D4L2Z7C.png', 'https://www.PetFriendSupply.com/img/img5.png', 0, NULL, 0),
(3, '3W5O1O0H', 'Extra Small Animal Carrier', '\"[{\"text\":\"Carrier for small animals and critters.\"},{\"text\":\"Great for hamsters, rodents, reptiles and more.\"},{\"text\":\"See through top and well ventilated.\"},{\"text\":\"Water bottle included.\"},{\"text\":\"Internal Dimensions are 8 \\u00be x 4 \\u00be, with a height of 4 \\u00bc to the top of the basket, excluding the dome.\"},{\"text\":\"Dimensions: 10 x 7.17 x 6.38 inches. Weight: 8.1 ounces.\"}]\"', 19.99, 'pet', 'img3_3W5O1O0H.png', 'https://www.CuteFriendlyPet.com/img/img2.png', 0, NULL, 0),
(2, '4G6B1P2G', 'Extra-Small Nail Nipper', '\"[{\"text\":\"EXTRA-SMALL CAT NAIL CLIPPERS: Safely cut your pet\'s nails with dog & cat nail clippers. Featuring stainless steel blades these clippers are great for kittens.\"},{\"text\":\"COMFORT & CONFIDENCE: Featuring a soft, ergonomic non-slip grip for better control & less hand fatigue, dog & cat nail clippers & grinders are comfortable & safe for you & your pet!\"},{\"text\":\"PET GROOMING SUPPLIES: Grooming your cat or dog is easy when you have the right tools. We make professional-quality brushes, shears, clippers, nail grinders & more to keep your pet looking great!\"},{\"text\":\"MADE TO LAST: Dog & Cat pet grooming tools are designed with high-quality materials for durability & performance.\"},{\"text\":\"Dimensions: 0.38 x 2.63 x 5.38 inches. Weight: 4 Ounces.\"}]\"', 7.95, 'pet', 'img2_4G6B1P2G.png', 'https://www.YourPetTime.com/img/img17.png', 0, NULL, 0),
(8, '5V4N5R1X', 'Faux Fur Bagel Pet/Dog Bed', '\"[{\"text\":\"360-degree zipper easy to assemble and to clean.\"},{\"text\":\"Cover is machine washable\\/dryer friendly.\"},{\"text\":\"Reversible (can be used in two different sides). Long lasting, unique durable fabrics.\"},{\"text\":\"Water proof pillow liner.\"},{\"text\":\"Dimensions: 42 x 42 x 14 inches. Weight: 13 Pounds.\"}]\"', 115.89, 'pet', 'img8_5V4N5R1X.png', 'https://www.DreamPetKingdom.com/img/img9.png', 0, NULL, 0),
(8, '5F3H7M5X', 'Faux Fur Orthopedic Dog Bed', '\"[{\"text\":\"As a pet parent, you want your dog to stay comfortable whether it\\u2019s the day or night. We\\u2019re all about creating pet care products that offer just the right balance of comfort, style, and durability. We designed this soft, self-warming dog bed to provide your four-legged friend the perfect spot to rest, sleep, and lounge anytime they want so they can relax comfortably in style! Our waterproof dog faux fur bed doubles as a decorative area rug along with offering your pet the comfort and support of orthopedic memory foam while looking classy and elegant to blend in and complement the look of your d\\u00e9cor. Just lay it out and never worry about having to hide it when you have guests over.\"},{\"text\":\"Dimensions: 48.43 x 28.35 x 2.36 inches; Weight: 5.27 pounds; Color: Gray;\"}]\"', 115.89, 'pet', 'img8_5F3H7M5X.png', 'https://www.LoveOnlyPetStore.com/img/img9.jpg', 0, NULL, 0),
(6, '1P0Y8E0U', 'Feed Horse Bag', '\"[{\"text\":\"Holds 1 Bale Of Hay - this 19x19x41\\\" hay bag with 1.75\\\" square openings holds approx. 1 bale of Timothy, Orchard, Alfalfa, or any other type of hay. Our hay bags were designed to minimize frustration and can be stuffed in seconds.\"}]\"', 69.99, 'pet', 'img6_1P0Y8E0U.png', 'https://www.PetLoveMaster.com/img/img6.png', 0, NULL, 0),
(8, '1F6Q5D2T', 'Filter for Aquarium', '\"[{\"title\":\"3-STAGE FILTRATION\",\"text\":\"Water flows through media layers to filter water and ensure a clean aquatic environment.\"},{\"title\":\"VERSATILE\",\"text\":\"For freshwater and saltwater aquariums.\"},{\"title\":\"EASY MAINTENANCE\",\"text\":\"Valve block for quick release to shut down water flow \\u2013 separates from housing for quick, spill-free maintenance.\"},{\"title\":\"THREE SIZES AVAILABLE\",\"text\":\"Comes in 160 GPH, 220 GPH and 360 GPH sizes.\"}]\"', 109, 'pet', 'img8_1F6Q5D2T.png', 'https://www.LifeStayWithPet.com/img/img8.png', 0, NULL, 0);
