(2, '5W6R2A6A', 'Hang-IN-Tent Sleeper', '\"[{\"text\":\"Safe and fun double fleece hanging sleeper for small animals.\"},{\"text\":\"Made of super soft fleece with no foam - washable.\"},{\"text\":\"Fun place for small pets to play and hide.\"},{\"text\":\"Hanging design keeps pets clean and comfortable while expanding your pets living space.\"},{\"text\":\"Fits inside any cage, durable nylon straps and clips allow for easy attachment.\"},{\"text\":\"Dimensions: 12 x 9 x 10 inches. Weight: 7 ounces.\"}]\"', 6.25, 'pet', 'img2_5W6R2A6A.png', 'https://www.DreamFluffyDailyCare.com/img/img15.png', 0, NULL, 0),
(2, '1I1P6R2J', 'Hanging Bowl', '\"[{\"text\":\"High-quality Construction \\u2013 The stainless steel pet food bowl stands up to rough treatment from pets, as it will not rust or chip.\"},{\"text\":\"Easy Clean-up Process \\u2013 You can easily remove pet food bowl from the cage for cleaning following meal time.\"},{\"text\":\"8-Ounce Capacity Size \\u2013 This hanging food bowl has an 8-ounce food capacity; four other sizes are available up to 64 ounces.\"},{\"text\":\"Simple Hook Design \\u2013 The easy to use pair of hooks secures the pet bowl to the cage.\"},{\"text\":\"Good Size for Small Pets \\u2013 With only 3 inches of depth for this bowl, even small pets can easily reach the bottom of the bowl.\"},{\"text\":\"Dimensions: 5.3 x 4.3 x 3 inches. Weight: 0.22 Pounds.\"}]\"', 5.95, 'pet', 'img2_1I1P6R2J.png', 'https://www.ThePerfectPetGoods.com/img/img1.png', 0, NULL, 0),
(2, '2A7Q8V6V', 'Hanging Toy for Small Animals', '\"[{\"text\":\"A unique combination of volcanic pumice rock material and natural wood pieces.\"},{\"text\":\"Lava and wood materials help keep pet\'s teeth trim and clean.\"},{\"text\":\"Includes natural wood hook for hanging.\"},{\"text\":\"Ideal for chinchillas, rabbits or other small animals.\"},{\"text\":\"Actual size approximately 9-1\\/2-inch long and 2-1\\/2-inch wide.\"}]\"', 5.95, 'pet', 'img2_2A7Q8V6V.png', 'https://www.marketforpetmaster.com/img/img1.png', 0, NULL, 0),
