(5, '8F7L7R4R', 'Mouth Water Bottle', '\"[{\"text\":\"Double wall vacuum insulation keeps beverages cold up to 24 hours.\"},{\"text\":\"Made with 18\\/8 pro-grade stainless steel to ensure pure taste and no flavor transfer \\u2014 and the durable construction stands up to whatever life brings.\"},{\"text\":\"Our proprietary powder coat means an easy-grip, sweat-free, and extra-durable bottle that you can take anywhere.\"}]\"', 39.99, 'pet', 'img5_8F7L7R4R.png', 'https://www.CoolBodyGym.com/img/img4.jpg', 0, NULL, 0),
(2, '4P5S4X0L', 'Mouthguard', '\"[{\"text\":\"It\'s perfect for those situations where you need a safe, secure mouthguard right this second. It\\u2019s protection where you need it most, when you need it most.\"},{\"text\":\"No boiling or moulding needed: Straight out of the case and into your mouth for immediate gameplay.\"},{\"text\":\"Adult sizes suitable for ages 10+, kids sizes suitable for ages up to 10. Not compatible with braces.\"},{\"text\":\"Suitable for all ball, stick, and combat sports, great to keep in your kit bag as a spare on match days or for activities where a mouthguard is recommended but not necessarily required.\"}]\"', 5.95, 'pet', 'img2_4P5S4X0L.png', 'https://www.CoolBodyGym.com/img/img1.jpg', 0, NULL, 0),
(3, '6T2V6N0U', 'Moving Cat Kicker Toy', '\"[{\"text\":\"Interactive Fun for Cats: every time your feline touches this cat kicker toy, the automatic built-in motion sensor kicks into action and the cat teaser fish moves in a wiggling way, intriguing your cat to kick and play. Perfect Thanksgiving, Christmas and New Year gift for pet owners or your own pets\"},{\"text\":\"Dimensions: 11.65 x 5 x 1.3 inches; Weight: 3.5 ounces; Color: Gray\"}]\"', 19.99, 'pet', 'img3_6T2V6N0U.png', 'https://www.LivesWithPet.com/img/img2.jpg', 0, NULL, 0),
(8, '8A4X3V2P', 'Multi-Level Cat Tree for Large Cats', '\"[{\"text\":\"With 5 levels design, this cat tree is large enough for your baby cat to play, sleep and do some exercises. Every platform has its own special functions, and your cat can totally enjoy himself on this cat tree for one day. If you have more than one cat? Then this cat tree is perfect for you to choose.\"},{\"text\":\"Dimensions: 22.44 x 19.29 x 8.66 inches; Weight: 39.82 pounds; Color: Light Gray; Quantity: 1 Cat Tree\"}]\"', 109, 'pet', 'img8_8A4X3V2P.png', 'https://www.ThePapperPet.com/img/img8.jpg', 0, NULL, 0),
(4, '5W4Q7J5F', 'Nail Grinder', '\"[{\"text\":\"Professional Grade Pet Nail Grinder: Easy to use, advanced grooming tool perfect for both pet owners and professional groomers. Grinder safely trims and smooths the nails of small to medium-sized pets, quickly and comfortably, to preferred length without snagging\\/splitting nails or damaging wicks.\"},{\"text\":\"Convertible, Diamond Bit Grinding Head: A durable, premium carborundum grinding wheel with three-port options, effectively supports a wide variety of dogs and cats with varying claw lengths and thickness.\"},{\"text\":\"Adjustable, HIGH\\/LOW Dual-Speed Settings: LOW-speed mode allows you to efficiently ease pets into their nail trimming session, then switch to HIGH-speed mode for particularly sharp or rough nails.\"},{\"text\":\"Wireless with Long-Lasting, Rechargeable Battery: 2000mAh large capacity battery lasts 2x longer than the average nail grinder battery and provides powerful, sustainable grinding with heavy-duty gear. Nail Grinder can be used while charging or wirelessly for up to 6 hours, after a full 3-hour charge with included USB charger and Type-C cable.\"},{\"text\":\"Ultra Quiet, Low Vibration Motor: Reduced noise caters to pets struggling with anxiety or that become nervous when having their nails trimmed, providing a more tolerable, relaxed experience with smooth, steady grinding and gentle vibration.\"},{\"text\":\"Dimensions: 7.4 x 2.99 x 2.32 inches. Weight: 9.6 ounces.\"}]\"', 24.99, 'pet', 'img4_5W4Q7J5F.png', 'https://www.CuteFriendlyPet.com/img/img3.png', 0, NULL, 0),
(2, '1H4M2F1D', 'Nail Trimmer', '\"[{\"text\":\"Professional quality. Helps prevent nails from breaking and snagging.\"},{\"text\":\"Easy to use.\"},{\"text\":\"Dimensions: 4 x 2.15 x 0.2 inches. Weight: 0.02 Pounds.\"}]\"', 7.95, 'pet', 'img2_1H4M2F1D.png', 'https://www.HomiePupFriends.com/img/img22.png', 0, NULL, 0),
(5, '1L0Y4O4G', 'No-Mess Hooded Cat Litter Box', '\"[{\"text\":\"This litter box makes a great choice for cats that prefer an enclosed space instead of an open-pan litter box.\"},{\"text\":\"The dome-style cover snaps into place. It lifts off with help from the top handle for full, easy access when scooping or changing the litter.\"},{\"text\":\"Easy in, easy out\\u2014the cat litter box provides a swinging plastic door that kitty can push past, whether coming or going.\"},{\"text\":\"Dimensions: 26.26 x 20.2 x 22.87 inches; Weight: 3.95 Pounds; Target Species: Cat; Material: Plastic; Color: Light Brown\"}]\"', 39.99, 'pet', 'img5_1L0Y4O4G.png', 'https://www.CharmingPetStore.com/img/img4.png', 0, NULL, 0),
(4, '1N2B2H5F', 'No-Pull Pet Harness with 2 Leash Clips', '\"[{\"text\":\"Recommended Breeds: Medium to Large Dogs, such as Golden Retriever, Huskie, Labrador, Alaska, German Shepherd, Akita, etc. Please measure the size carefully before purchasing this present for your puppy. Make your puppy stand out with this Classic Black Harness!\"},{\"text\":\"Dimensions: 0.59 x 9.09 x 14.21 inches; Weight: 10.6 ounces; Color: Black\"}]\"', 24.99, 'pet', 'img4_1N2B2H5F.png', 'https://www.ToTheSkyPet.com/img/img3.jpg', 0, NULL, 0),
(2, '8D7F1W5H', 'Nursing Bottle for Pets', '\"[{\"text\":\"Designed for feeding baby animals like puppies and kittens and other newborn animals. Capacity: 50cc.\"},{\"text\":\"Dimensions: 0.79 x 0.79 x 0.79 inches. Weight: 1.83 Ounces.\"}]\"', 6.95, 'pet', 'img2_8D7F1W5H.png', 'https://www.LovelyBabyPaws.com/img/img16.png', 0, NULL, 0),
