(4, '7L0N3L1I', 'Pet Cloak', '\"[{\"text\":\"This pet cape is made of high-grade materials and exquisitely made, which is very suitable for cats and dogs and other pets to wear in winter. It is warm and cute. And it is deeply loved by buyers.\"},{\"text\":\"Gender: Unisex; Size: M (within 7.5kg for cats, within 4kg for dogs); Weight: 0.18 ounces; Main Stones: Amethyst; Material: Lamb Wool; Color: Gray and Yellow; Type: Jumpsuit\"}]\"', 24.99, 'pet', 'img4_7L0N3L1I.png', 'https://www.BestfriendPetStorePro.com/img/img5.png', 0, NULL, 0),
(9, '5P2M5E4O', 'Pet Coat', '\"[{\"text\":\"Winter fleece jumper with integrated harness.\"},{\"text\":\"Checkered pattern and solid schemed.\"},{\"text\":\"Decorated with waste dispenser on the back.\"},{\"text\":\"Size: Large : 12.6\\\" Neck, 18.9\\\" Chest, 12.2\\\" Product Length.\"}]\"', 132, 'pet', 'img9_5P2M5E4O.png', 'https://www.FlurryFunKingdom.com/img/img9.png', 0, NULL, 0),
(2, '2J8J7S1C', 'Pet Comb and Hair Dryer', '\"[{\"text\":\"Groom your furry friend with this easy-to-use pet hair dryer that comes with a brush.\"},{\"text\":\"Dimensions: 12.59 x 3.54 x 1.97 inches; Weigh: 0.44 pounds; Material: Polyamide, Polycarbonate and Acrylonitrile Butadiene Styrene; Dog Size: Any; Color: Blue and White\"}]\"', 5.95, 'pet', 'img2_2J8J7S1C.png', 'https://www.BestPetsWonderland.com/img/img1.png', 0, NULL, 0),
(10, '1Q1U3R0P', 'Pet Cordless Clipper', '\"[{\"text\":\"The original 5-in-1 clipper, tried-and-true cordless option for precise trimming in hard-to-reach spots such as ears, faces, and paws to give groomers, animal professionals, and pet owners everything needed in a lightweight cordless clipper.\"},{\"text\":\"The 5-in-1 fine blade adjusts between sizes #9, 10, 15, 30, and 40, offering a convenient variety of cutting lengths. With a rechargeable battery, this clipper has an 80-minute cordless run time and 75 minute charge time; it\'s easy to clean with a no-maintenance motor.\"},{\"text\":\"Pet clipper can be used on small and medium-sized dogs such as Bichons, Shih Tzus, Terriers, and Spaniels, as well as cats; can also be used as a horse clipper and is great for trimming and touch-ups on the muzzle, face, ears, legs, fetlocks, and bridle paths.\"},{\"text\":\"The slim, lightweight design fits comfortably in your hand while reducing wrist fatigue.\"},{\"text\":\"Made for use in electrical outlets in the United States only; not designed for international use.\"},{\"text\":\"Includes one cordless clipper, 5-in-1 fine blade set, 2 drop-in  rechargeable battery packs, a soft storage case, charging stand and charger, 4 plastic attachment guide combs, cleaning brush, blade oil, and instruction book.\"},{\"text\":\"Dimensions: 7 x 1.44 x 1.69 inches; Weight: 8 Ounces.\"}]\"', 149, 'pet', 'img10_1Q1U3R0P.png', 'https://www.LovelyBabyPaws.com/img/img13.png', 0, NULL, 0),
(6, '5B3H4R1I', 'Pet Dog Cat Carrier Trolley Case Space Capsule', '\"[{\"text\":\"No matter your mode of travel, carrying your small dog or cat around is easier than ever with this soft sided bag.\"},{\"text\":\"Dimensions: \\t19 x 13 x 15 inches; Weight: 4.9 pounds; Color: Gray\"}]\"', 69.99, 'pet', 'img6_5B3H4R1I.png', 'https://www.CutiePetStore.com/img/img6.jpg', 0, NULL, 0),
(4, '7E1G6O0D', 'Pet Dog Cat Grooming Bath Brush', '\"[{\"text\":\"This pet brush has a shampoo dispensing design, and not only can provide a quick and thorough washing of your pets, but also massage the skin to improve the blood circulation of them.\"},{\"text\":\"Dimensions: 3.15 x 2.36 inches; Weight: 0.9 ounces; Color: Cyan\"}]\"', 24.99, 'pet', 'img4_7E1G6O0D.png', 'https://www.FluffyPetStorePro.com/img/img3.jpg', 0, NULL, 0),
(7, '3C8B2M2C', 'Pet Dog Grooming Table', '\"[{\"text\":\"This grooming table legs are made of heavy duty iron. The corners of the table are rounded to prevent your pet from being unnecessarily hurt in the struggle. In addition, the metal legs and non-slip rubber on the feet increases the stability and durability of the table.\"},{\"text\":\"Dimensions: \\t33.5 x 21 x 4.5 inches; Weight: 23.75 pounds; Color: Pink\"}]\"', 89.99, 'pet', 'img7_3C8B2M2C.png', 'https://www.AllTheBestPet.com/img/img6.jpg', 0, NULL, 0),
