(4, '0L8H6W4B', 'Training Pad Holder for Puppy', '\"[{\"text\":\"Hold pet training pads in place and prevent damage from scratching, tearing, and chewing with this easy to clean pet pad tray and grate.\"},{\"text\":\"Double latch closure locks regular puppy pads securely in place, while 4 non-skid rubber feet protect floor from scratches.\"},{\"text\":\"Dog potty pad holder has a high polish finish for easy cleaning.\"},{\"text\":\"Designed to hold regular pet training pads.\"},{\"text\":\"Dimensions: 25.55\\\"L x 18.90\\\"W x 1.75\\\"H.\"}]\"', 24.99, 'pet', 'img4_0L8H6W4B.png', 'https://www.DreamPetWorld.com/img/img3.png', 0, NULL, 0),
(10, '7F1O7A7F', 'Transparent Umbrellas for Pets', '\"[{\"text\":\"Umbrella with Chain Leash.\"},{\"text\":\"Dimensions: 17.56 x 4.33 x 1.46 inches; Weight: 12.32 ounces; Color: Transparent\"}]\"', 149, 'pet', 'img10_7F1O7A7F.png', 'https://www.CutiePetStore.com/img/img13.jpg', 0, NULL, 0),
(7, '1Z0R0Q2K', 'Travel Backpack', '\"[{\"text\":\"Mobile pet bed, in-cabin airplane carrier, and car seat all in one (please check airline restrictions before purchase). Adjustable backpack straps and carrying handle make our carrier easy to attach to a seatbelt.\"},{\"text\":\"Built-In security leash, mesh panels & ventilation holes, soft washable pad, top & side entries for maximum accessibility.\"}]\"', 89.99, 'pet', 'img7_1Z0R0Q2K.png', 'https://www.AnimalFairPetStore.com/img/img6.jpg', 0, NULL, 0),
(3, '8P4W1H2K', 'Travel Pet Kennel', '\"[{\"text\":\"Portable pop up dog crate is great for travel, camping or hiking.\"},{\"text\":\"Soft sided with mesh screen windows for visibility and ventilation keep pets contained and comfortable.\"},{\"text\":\"Suitable for small to medium size dogs, cats and other pets.\"},{\"text\":\"Easily pops up and folds flat to 11\\\" round. Comes with a convenient storage pouch, for backpack or luggage.\"},{\"text\":\"Use indoors or outdoors.\"},{\"text\":\"Dimensions: 23.23 x 15.47 x 3.11 inches. Weight: 11.7 ounces.\"}]\"', 19.99, 'pet', 'img3_8P4W1H2K.png', 'https://www.SnugglePawsPro.com/img/img2.png', 0, NULL, 0);
